mapper.delete:
delete from ALR_STD_INVOICE_DTL_RECORD
where FILE_ID = #{fileId,jdbcType=VARCHAR}
  and INVOICE_ID = #{invoiceId,jdbcType=VARCHAR}
  and INVOICE_CODE = #{invoiceCode,jdbcType=VARCHAR}

mapper.select:
select FILE_ID, INVOICE_ID, INVOICE_CODE, BUYER_NAME, BUYER_TAX_CODE, BANK_BRANCH, 
BANK_ACCOUNT, ADDRESS, TEL, INVOICE_DATE, PRODUCT_VERSION, RECEIPT_ID, PRODUCT_NAME, 
PRODUCT_SPEC, PRODUCT_UNIT, PRODUCT_COUNT, PRODUCT_UNIT_PRICE, TOTAL_PRICE, TAX_RATE, 
TAX, TAX_TYPE_CODE
from ALR_STD_INVOICE_DTL_RECORD
where FILE_ID = #{fileId,jdbcType=VARCHAR}
  and INVOICE_ID = #{invoiceId,jdbcType=VARCHAR}
  and INVOICE_CODE = #{invoiceCode,jdbcType=VARCHAR}
