mapper.delete:
delete from AUTH_MENU_ROLE_REL
where MENU_ID = #{menuId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}

mapper.insert:
insert into AUTH_MENU_ROLE_REL (MENU_ID, ROLE_ID, UPDATE_DATE
  )
values (#{menuId,jdbcType=VARCHAR}, #{roleId,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}
  )

mapper.update:
update AUTH_MENU_ROLE_REL
set UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
where MENU_ID = #{menuId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}

mapper.select:
select MENU_ID, ROLE_ID, UPDATE_DATE
from AUTH_MENU_ROLE_REL
where MENU_ID = #{menuId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}
