mapper.delete:
delete from CRM_CUSTOM_BANK_INFO
where CUSTOM_ID = #{customId,jdbcType=VARCHAR}
  and BANK_ID = #{bankId,jdbcType=VARCHAR}

mapper.insert:
insert into CRM_CUSTOM_BANK_INFO (CUSTOM_ID, BANK_ID, UPDATE_DATE
  )
values (#{customId,jdbcType=VARCHAR}, #{bankId,jdbcType=VARCHAR}, #{updateDate,jdbcType=DATE}
  )

mapper.update:
update CRM_CUSTOM_BANK_INFO
set UPDATE_DATE = #{updateDate,jdbcType=DATE}
where CUSTOM_ID = #{customId,jdbcType=VARCHAR}
  and BANK_ID = #{bankId,jdbcType=VARCHAR}

mapper.select:
select CUSTOM_ID, BANK_ID, UPDATE_DATE
from CRM_CUSTOM_BANK_INFO
where CUSTOM_ID = #{customId,jdbcType=VARCHAR}
  and BANK_ID = #{bankId,jdbcType=VARCHAR}
