mapper.insert:
insert into CRM_CUSTOM_FINANCE_INFO (CUSTOM_ID, FINANCE_DISK_TYPE, UPDATE_DATE
  )
values (#{customId,jdbcType=VARCHAR}, #{financeDiskType,jdbcType=VARCHAR}, #{updateDate,jdbcType=DATE}
  )

mapper.update:
update CRM_CUSTOM_FINANCE_INFO
set FINANCE_DISK_TYPE = #{financeDiskType,jdbcType=VARCHAR},
  UPDATE_DATE = #{updateDate,jdbcType=DATE}
where CUSTOM_ID = #{customId,jdbcType=VARCHAR}

mapper.select:
select CUSTOM_ID, FINANCE_DISK_TYPE, UPDATE_DATE
from CRM_CUSTOM_FINANCE_INFO
where CUSTOM_ID = #{customId,jdbcType=VARCHAR}
