mapper.update:
update CRM_CUSTOM_ORDER_INFO
set CUSTOM_ID = #{customId,jdbcType=VARCHAR},
  ORDER_DATE = #{orderDate,jdbcType=VARCHAR},
  RECEIVABLE_AMT = #{receivableAmt,jdbcType=DECIMAL},
  PAYABLE_AMT = #{payableAmt,jdbcType=DECIMAL}
where ORDER_ID = #{orderId,jdbcType=VARCHAR}

mapper.select:
select ORDER_ID, CUSTOM_ID, ORDER_DATE, RECEIVABLE_AMT, PAYABLE_AMT
from CRM_CUSTOM_ORDER_INFO
where ORDER_ID = #{orderId,jdbcType=VARCHAR}
