mapper.update:
update CRM_ORDER_DLJZ_DETAIL
set ORDER_ID = #{orderId,jdbcType=VARCHAR},
  BEGIN_DATE = #{beginDate,jdbcType=DATE},
  END_DATE = #{endDate,jdbcType=DATE},
  IS_BEGIN = #{isBegin,jdbcType=VARCHAR}
where EVENT_ID = #{eventId,jdbcType=VARCHAR}

mapper.select:
select EVENT_ID, ORDER_ID, BEGIN_DATE, END_DATE, IS_BEGIN,DIFF
from CRM_ORDER_DLJZ_DETAIL
where EVENT_ID = #{eventId,jdbcType=VARCHAR}
