mapper.update:
update CRM_ORDER_GSZC_DETAIL
set ORDER_ID = #{orderId,jdbcType=VARCHAR},
  ABSENT = #{absent,jdbcType=VARCHAR},
  REG_LOCATION_TYPE = #{regLocationType,jdbcType=VARCHAR}
where EVENT_ID = #{eventId,jdbcType=VARCHAR}

mapper.select:
select EVENT_ID, ORDER_ID, ABSENT, REG_LOCATION_TYPE
from CRM_ORDER_GSZC_DETAIL
where EVENT_ID = #{eventId,jdbcType=VARCHAR}
