mapper.delete:
delete from CRM_COMPANY_BUSINESS
where COMPANY_ID = #{companyId,jdbcType=VARCHAR}
  and BUSINESS_ID = #{businessId,jdbcType=VARCHAR}

mapper.insert:
insert into CRM_COMPANY_BUSINESS (COMPANY_ID, BUSINESS_ID, BEGIN_DATE, 
  END_DATE, UPDATE_DATE, IS_VALID
  )
values (#{companyId,jdbcType=VARCHAR}, #{businessId,jdbcType=VARCHAR}, #{beginDate,jdbcType=DATE}, 
  #{endDate,jdbcType=DATE}, #{updateDate,jdbcType=DATE}, #{isValid,jdbcType=VARCHAR}
  )

mapper.update:
update CRM_COMPANY_BUSINESS
set BEGIN_DATE = #{beginDate,jdbcType=DATE},
  END_DATE = #{endDate,jdbcType=DATE},
  UPDATE_DATE = #{updateDate,jdbcType=DATE},
  IS_VALID = #{isValid,jdbcType=VARCHAR}
where COMPANY_ID = #{companyId,jdbcType=VARCHAR}
  and BUSINESS_ID = #{businessId,jdbcType=VARCHAR}

mapper.select:
select COMPANY_ID, BUSINESS_ID, BEGIN_DATE, END_DATE, UPDATE_DATE, IS_VALID
from CRM_COMPANY_BUSINESS
where COMPANY_ID = #{companyId,jdbcType=VARCHAR}
  and BUSINESS_ID = #{businessId,jdbcType=VARCHAR}
