mapper.delete:
delete from workflow_def_graph
where graph_id = #{graphId,jdbcType=VARCHAR}
  and root_node = #{rootNode,jdbcType=VARCHAR}

mapper.insert:
insert into workflow_def_graph (graph_id, root_node, name
  )
values (#{graphId,jdbcType=VARCHAR}, #{rootNode,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}
  )

mapper.update:
update workflow_def_graph
set name = #{name,jdbcType=VARCHAR}
where graph_id = #{graphId,jdbcType=VARCHAR}
  and root_node = #{rootNode,jdbcType=VARCHAR}

mapper.select:
select graph_id, root_node, name
from workflow_def_graph
where graph_id = #{graphId,jdbcType=VARCHAR}
  and root_node = #{rootNode,jdbcType=VARCHAR}
