mapper.insert:
insert into workflow_event (event_id, graph_id, cur_node_id, event_owner, cur_operator,cur_operator_name, begin_date,end_date ) select #{event.eventId}, t2.graph_id,
#{event.curNodeId},
t3.target_node,
#{event.eventOwner}, #{event.curOperator}, #{event.curOperatorName}, #{event.beginDate}, DATE_ADD(#{event.beginDate},INTERVAL t4.rest_days DAY) from workflow_def_graph_business_rel t1,workflow_def_graph t2,workflow_def_edge t3 left join workflow_def_node_ext_attr t4 on t3.target_node = t4.node_id where t1.graph_id = t2.graph_id and t2.root_node = t3.source_node and t1.business_id = #{businessId}
and t3.target_node = #{event.curNodeId}

mapper.select:
SELECT t1.next_node_id preNodeId,t1.node_id curNodeId,t2.role FROM workflow_event_history t1,workflow_def_node t2
WHERE t1.node_id = t2.node_id
and  t1.event_id = #{eventId}
and t1.next_node_id = #{nodeId}

SELECT T1.source_node preNodeId,T1.target_node curNodeId,T2.role FROM workflow_def_edge T1,workflow_def_node T2
WHERE T1.target_node = T2.node_id
AND T1.source_node = #{nodeId}
