mapper.select:
select log_id logId, operator, operator_name operatorName, create_time createTime, message, event_id eventId, has_attachment hasAttachment, attachments_path attachmentsPath, has_refund hasRefund, refund_fee refundFee from workflow_log_message
event_id = #{eventId}
and operator is null
operator = #{item}
and node_id = #{curNodeId}
order by create_time desc
