mapper.delete:
delete from HRM_ROLE_AUTH
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ITEM = #{item,jdbcType=VARCHAR}

mapper.update:
update HRM_ROLE_AUTH
set AUTH_TYPE = #{authType,jdbcType=VARCHAR},
  ITEM_TYPE = #{itemType,jdbcType=VARCHAR}
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ITEM = #{item,jdbcType=VARCHAR}

mapper.select:
select ROLE_ID, ITEM, AUTH_TYPE, ITEM_TYPE
from HRM_ROLE_AUTH
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ITEM = #{item,jdbcType=VARCHAR}
