mapper.insert:
insert into HRM_ROLE_INFO (ROLE_ID, ROLE_NAME, PARENT_ROLE
  )
values (#{roleId,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, #{parentRole,jdbcType=VARCHAR}
  )

mapper.update:
update HRM_ROLE_INFO
set ROLE_NAME = #{roleName,jdbcType=VARCHAR},
  PARENT_ROLE = #{parentRole,jdbcType=VARCHAR}
where ROLE_ID = #{roleId,jdbcType=VARCHAR}

mapper.select:
select ROLE_ID, ROLE_NAME, PARENT_ROLE
from HRM_ROLE_INFO
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
