mapper.delete:
delete from HRM_USER_ROLE_REL
where USER_ID = #{userId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}

mapper.insert:
insert into HRM_USER_ROLE_REL (USER_ID, ROLE_ID, UPDATE_DATE
  )
values (#{userId,jdbcType=VARCHAR}, #{roleId,jdbcType=VARCHAR}, #{updateDate,jdbcType=DATE}
  )

mapper.update:
update HRM_USER_ROLE_REL
set UPDATE_DATE = #{updateDate,jdbcType=DATE}
where USER_ID = #{userId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}

mapper.select:
select USER_ID, ROLE_ID, UPDATE_DATE
from HRM_USER_ROLE_REL
where USER_ID = #{userId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}
